import jwt_decode from "jwt-decode";

export const doLogin = (email, password) => {
	let promiseResolveRef = null;
	let promiseRejectRef = null;
	let promise = new Promise((resolve, reject) => {
		promiseResolveRef = resolve;
		promiseRejectRef = reject;
	});

	// Manually specify the token value here
	const hardcodedToken = "eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJhZG1pbkBkZW1vLmNvbSIsImlhdCI6MTczMDEzNDc5MCwiZXhwIjoxNzMwMTQzMTkwfQ.cc3CoVGPvOLPtX0Qx1opGRSgh0G2MwD2vY0USeXNNLFayu0EYhE1nAuua37cb_O8dR-maoNyb1p4HSIN1t5CAw";

	// Fetch request for login
	fetch('http://localhost:8080/api/auth/signin', {
		method: 'POST',
		body: JSON.stringify({
			username: email,
			password: password,
		}),
		headers: {
			'Content-type': 'application/json; charset=UTF-8',
		},
	})
	.then((response) => {
		response.json().then((json) => {
			if (response.ok) {
				// Use the manually specified token instead of fetching from headers
				const token = hardcodedToken;
				const decoded = jwt_decode(token);

				promiseResolveRef({
					username: json.email,
					accessToken: token,
					accessTokenTimeout: decoded.exp * 1000, // Convert expiration to milliseconds
					roles: json.roles,
					userId: json.id,
					response: response,
				});
			} else {
				promiseRejectRef({
					reason: "Server error occurred. Please try again.",
					response: response,
				});
			}
		}).catch((error) => {
			promiseRejectRef({
				reason: "Bad Credentials. Please try again.",
				response: error,
			});
		});
	}).catch((err) => {
		promiseRejectRef({
			reason: "Some error occurred. Please try again.",
			response: err,
		});
	});
	return promise;
};
